import React, { useState, useEffect } from 'react';
import './App.css';
import Musicplayer from './Musicplayer.js';
import Loader from './Loader.js'; // Import your Loader component
import audioClip from './loader.mp3'; // Import your audio clip

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [audioPlayed, setAudioPlayed] = useState(false);

  useEffect(() => {
    const audio = new Audio(audioClip);

    // Function to handle audio play
    const playAudio = () => {
      audio.play();
      setAudioPlayed(true);
      setTimeout(() => {
        setIsLoading(false);
      }, 3000); // Set isLoading to false after 3 seconds
    };

    // Check if audio has been played before
    const audioPlayedBefore = localStorage.getItem('audioPlayed');
    if (audioPlayedBefore === 'true') {
      setAudioPlayed(true);
      setIsLoading(false);
    }

    // Play audio and set isLoading to false if audio has not been played before
    if (!audioPlayedBefore) {
      playAudio();
      localStorage.setItem('audioPlayed', 'true');
    }

    // Clean up audio on component unmount
    return () => {
      audio.pause(); // Pause the audio if component unmounts
    };
  }, []);

  return (
    <>
      {isLoading ? (
        // Show the loader component while isLoading is true
        <div className="loader-container">
          <Loader />
        </div>
      ) : (
        // Show the music player app when isLoading becomes false
        <Musicplayer />
      )}
    </>
  );
}

export default App;
