import React, { useState, useEffect } from 'react';
import './App.css';
import Musicplayer from './Musicplayer.js';
import Loader from './Loader.js'; // Import your Loader component
import audioClip from './loader.mp3'; // Import your audio clip

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [audioPlayed, setAudioPlayed] = useState(false); // Track whether the audio has been played

  useEffect(() => {
    const audio = new Audio(audioClip);

    // Function to handle audio play
    const playAudio = () => {
      audio.play();
      setAudioPlayed(true);
    };

    // Simulate loading for 5 seconds
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 5000);

    // Play audio only if it hasn't been played yet
    if (!audioPlayed) {
      playAudio();
    }

    return () => {
      clearTimeout(timer);
      audio.pause(); // Pause the audio if component unmounts
    };
  }, [audioPlayed]); // Run effect when audioPlayed state changes

  return (
    <>
      {isLoading ? (
        // Show the loader component while isLoading is true
        <Loader />
      ) : (
        // Show the music player app when isLoading becomes false
        <Musicplayer />
      )}
    </>
  );
}

export default App;
