import React, { useState, useRef, useEffect } from "react";
import "./Musicplayer.css";
import Loader from "./Loader";

const MusicPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [currentSongImage, setCurrentSongImage] = useState("");
  const [songs, setSongs] = useState([]);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const audioRef = useRef(null);
  const [audioLoaded, setAudioLoaded] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [volume, setVolume] = useState(0.5);
  const [loading, setLoading] = useState(true); // State for loading songs

  useEffect(() => {
    setLoading(true); // Show loader when fetching songs

    fetch("http://localhost:5000/api/songs/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch songs");
        }
        return response.json();
      })
      .then((data) => {
        setSongs(data);
        if (data.length > 0) {
          setCurrentSongImage(data[currentSongIndex].image);
          audioRef.current.src = data[currentSongIndex].audio;
        }
        setLoading(false); // Hide loader after fetching songs
      })
      .catch((error) => {
        console.error("Error fetching songs:", error);
        setLoading(false); // Hide loader if fetching fails
      });
  }, [currentSongIndex]);

  useEffect(() => {
    const storedIndex = localStorage.getItem("currentSongIndex");
    if (storedIndex !== null) {
      setCurrentSongIndex(parseInt(storedIndex));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("currentSongIndex", String(currentSongIndex + 1));
  }, [currentSongIndex]);

  // Function to handle playing the current song
  const handlePlay = () => {
    const playPromise = audioRef.current.play();
    if (playPromise !== undefined) {
      playPromise
        .then(() => {
          setIsPlaying(true);
        })
        .catch((error) => {
          console.error("Playback failed:", error);
        });
    }
  };

  // UseEffect to set up audio event listeners
  useEffect(() => {
    const onLoadedData = () => {
      setAudioLoaded(true);
    };

    const onPlay = () => {
      setIsPlaying(true);
    };

    const onPause = () => {
      setIsPlaying(false);
    };

    const onTimeUpdate = () => {
      setCurrentTime(audioRef.current.currentTime);
    };

    const onEnded = () => {
      handleNext();
    };

    if (audioRef.current) {
      audioRef.current.addEventListener("loadeddata", onLoadedData);
      audioRef.current.addEventListener("play", onPlay);
      audioRef.current.addEventListener("pause", onPause);
      audioRef.current.addEventListener("timeupdate", onTimeUpdate);
      audioRef.current.addEventListener("ended", onEnded);
    }

    return () => {
      if (audioRef.current) {
        audioRef.current.removeEventListener("loadeddata", onLoadedData);
        audioRef.current.removeEventListener("play", onPlay);
        audioRef.current.removeEventListener("pause", onPause);
        audioRef.current.removeEventListener("timeupdate", onTimeUpdate);
        audioRef.current.removeEventListener("ended", onEnded);
      }
    };
  }, [audioRef]);

  // Function to play the next song
  const handleNext = () => {
    const newIndex = (currentSongIndex + 1) % songs.length;
    setCurrentSongIndex(newIndex);
    setCurrentSongImage(songs[newIndex].image);
    audioRef.current.src = songs[newIndex].audio;
    audioRef.current.play(); // Play the next song
  };

  // Function to play the previous song
  const handlePrevious = () => {
    const newIndex = (currentSongIndex - 1 + songs.length) % songs.length;
    setCurrentSongIndex(newIndex);
    setCurrentSongImage(songs[newIndex].image);
    audioRef.current.src = songs[newIndex].audio;
    audioRef.current.play(); // Play the previous song
  };

  // Function to handle play/pause toggle
  const playPauseToggle = () => {
    if (isPlaying) {
      audioRef.current.pause();
      setIsPlaying(false); // Update isPlaying state to false when pausing
    } else {
      handlePlay();
      setIsPlaying(true); // Update isPlaying state to true when playing
    }
  };

  // Function to handle volume change
  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    if (!isNaN(newVolume) && isFinite(newVolume)) {
      audioRef.current.volume = newVolume;
      setVolume(newVolume);
    }
  };

  // Function to handle progress change
  const handleProgressChange = (e) => {
    audioRef.current.currentTime = (e.target.value * duration) / 100;
  };

  // Function to truncate text
  const truncateText = (text, maxLength) => {
    if (text.length <= maxLength) {
      return text;
    } else {
      return text.substring(0, maxLength) + "...";
    }
  };
  

  return (
    <div className="music-container">
      <div className="music-body">
        <div className="layer">
          <div className="volume-slider">
            <span className="bar"></span>
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={fwa}
              className="volumerange"
            />
          </div>
        </div>
        <div className="top-icons">
          <button className="volume btn" onClick={() => setVal()}>
            <i id="volumeicon" className="fas fa-volume-up"></i>
          </button>
          <div className="title_name">
            <h4>G S Music</h4>
          </div>

          <button className="list btn" onClick={() => setSearchdiv()}>
            <i className="fas fa-list"></i>
          </button>
        </div>
        <div className="music-img">
          <img src={currentSongImage} alt="song-images" id="img" />
        </div>
        <div className="music-info">
          <h2 id="title" className="scrolling-title">
            {songs.length > 0 ? songs[currentSongIndex].title : "No Title"}
          </h2>
          <span id="artist" >
            {songs.length > 0
              ? songs[currentSongIndex].artist
              : "No artist"}
          </span>
        </div>

        <audio
          id="audio"
          ref={audioRef}
          onLoadedData={() => setAudioLoaded(true)}
          onTimeUpdate={() => setCurrentTime(audioRef.current.currentTime)}
          onDurationChange={() => setDuration(audioRef.current.duration)}
        ></audio>
        <div className="music-progress">
          <div className="progress-bar">
            <span
              className="progress-line"
              style={{ width: `${(currentTime / duration) * 100}%` }}
            ></span>
            <input
              type="range"
              min="0"
              max="100"
              value={(currentTime / duration) * 100}
              className="progress"
              onChange={handleProgressChange}
            />
          </div>
          <div className="duration">
            <span className="current-time">{formatTime(currentTime)}</span>
            <span className="duration-time">{formatTime(duration)}</span>
          </div>
        </div>
        <div className="music-controls">
          <button className="repeat btn">
            <i className="fas fa-redo-alt"></i>
          </button>
          <div className="main-controls">
            <button className="prevbtn" onClick={handlePrevious}>
              <i className="fas fa-backward"></i>
            </button>
            <button className="playpause" onClick={playPauseToggle}>
              <i
                id="playpause-btn"
                className={`fas ${isPlaying ? "fa-pause" : "fa-play"}`}
              ></i>
            </button>
            <button className="nextbtn" onClick={handleNext}>
              <i className="fas fa-forward"></i>
            </button>
          </div>
          <button className="like btn">
            <i id="likeicon" className="far fa-heart"></i>
          </button>
        </div>
        <div className="songs-list">
          <button className="list-close btn">
            <i className="fas fa-times"></i>
          </button>
        </div>
      </div>

      <div className="song_filter">
        <div className="close" >
          <i className="fas fa-times"></i>
        </div>
        <input
          type="text"
          placeholder="Search songs..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="All">All Categories</option>
          <option value="Hindi">Hindi</option>
          <option value="English">English</option>
          <option value="Punjabi">Punjabi</option>
          <option value="Bhajpuri">Bhajpuri</option>
          <option value="Jhumar">Jhumar</option>
        </select>

        {filteredSongs.map((song, index) => (
          <div
            className="search_box"
            key={song.id}
            onClick={() => handleSongClick(index)}
          >
            <h5 className="scroll-text">{truncateText(song.title, 30)}</h5>
            <h5 className="scroll-text">{truncateText(song.artist, 10)}</h5>
          </div>
        ))}
      </div>
    </div>
  );
};

const formatTime = (time) => {
  const minutes = Math.floor(time / 60);
  const seconds = Math.floor(time % 60);
  return `${String(minutes).padStart(2, "0")}:${String(seconds).padStart(
    2,
    "0"
  )}`;
};

export default MusicPlayer;
