import React, { useState, useEffect } from 'react';
import './App.css';
import Musicplayer from './Musicplayer.js';
import Loader from './Loader.js'; // Import your Loader component
import audioClip from './loader.mp3'; // Import your audio clip

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Play the audio clip once when the component mounts
    const audio = new Audio(audioClip);
    const playPromise = audio.play();

    // If the audio starts playing successfully, set a timeout to stop the loader
    playPromise.then(() => {
      setTimeout(() => {
        setIsLoading(false);
        audio.pause(); // Pause the audio when loading is complete
      }, 5000); // Stop loader after 5 seconds
    }).catch(error => {
      console.error('Failed to play audio:', error);
      setIsLoading(false); // If audio fails to play, stop loader immediately
    });

    // Clean up audio and loader on component unmount
    return () => {
      clearTimeout();
      audio.pause();
    };
  }, []);

  // Show the loader component while isLoading is true
  if (isLoading) {
    return <Loader />;
  }

  // Show the music player app when isLoading becomes false
  return <Musicplayer />;
}

export default App;
