import React, { useState, useEffect } from 'react';
import "./Musicplayer.css";
import { FaPlay, FaPause, FaStepForward, FaStepBackward } from 'react-icons/fa';

function Musicplayer() {
    const [songs, setSongs] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('All');
    const [currentSong, setCurrentSong] = useState(null);
    const [isPlaying, setIsPlaying] = useState(false);

    useEffect(() => {
        fetch('http://localhost:5000/api/songs')
            .then(response => response.json())
            .then(data => setSongs(data))
            .catch(error => console.error('Error fetching songs:', error));
    }, []);

    useEffect(() => {
        const lastSongId = localStorage.getItem('lastSongId');
        if (lastSongId) {
            const lastSong = songs.find(song => song.id === parseInt(lastSongId));
            if (lastSong) {
                setCurrentSong(lastSong);
            }
        }
    }, [songs]);

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleCategoryChange = (event) => {
        setSelectedCategory(event.target.value);
    };

    const handleSongClick = (song) => {
        setCurrentSong(song);
        setIsPlaying(true);
        localStorage.setItem('lastSongId', song.id);
    };

    const handlePlayPauseClick = () => {
        setIsPlaying(prevState => !prevState);
    };

    const handleNextClick = () => {
        const currentIndex = songs.findIndex(song => song.id === currentSong.id);
        const nextIndex = (currentIndex + 1) % songs.length;
        setCurrentSong(songs[nextIndex]);
        setIsPlaying(true);
        localStorage.setItem('lastSongId', songs[nextIndex].id);
    };

    const handlePreviousClick = () => {
        const currentIndex = songs.findIndex(song => song.id === currentSong.id);
        const previousIndex = (currentIndex - 1 + songs.length) % songs.length;
        setCurrentSong(songs[previousIndex]);
        setIsPlaying(true);
        localStorage.setItem('lastSongId', songs[previousIndex].id);
    };

    const filteredSongs = songs.filter(song =>
        (song.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        song.artist.toLowerCase().includes(searchTerm.toLowerCase())) &&
        (selectedCategory === 'All' || song.categories.includes(selectedCategory))
    );

    return (
        <div className="music-player">
            <h1 className="title">GS Music Player</h1>
            <div className="controls">
                <input
                    type="text"
                    placeholder="Search for a song..."
                    value={searchTerm}
                    onChange={handleSearch}
                />
                <select value={selectedCategory} onChange={handleCategoryChange}>
                    <option value="All">All Categories</option>
                    <option value="English">English</option>
                    <option value="Hindi">Hindi</option>
                    <option value="Punjabi">Punjabi</option>
                    <option value="Odia">Odia</option>
                </select>
            </div>
            <div className="cd-container">
                {currentSong && (
                    <div className={`cd ${isPlaying ? 'playing' : ''}`}>
                        <img className="cd-image" src={currentSong.image} alt="Album Cover" />
                        <div className="controls">
                            <FaStepBackward className="control-button" onClick={handlePreviousClick} />
                            {isPlaying ? (
                                <FaPause className="control-button" onClick={handlePlayPauseClick} />
                            ) : (
                                <FaPlay className="control-button" onClick={handlePlayPauseClick} />
                            )}
                            <FaStepForward className="control-button" onClick={handleNextClick} />
                        </div>
                    </div>
                )}
            </div>
            <ul className="song-list">
                {filteredSongs.map(song => (
                    <li key={song.id} onClick={() => handleSongClick(song)}>
                        {song.title} - {song.artist}
                    </li>
                ))}
            </ul>
            {currentSong && (
                <audio
                    className="audio-player"
                    controls
                    autoPlay={isPlaying}
                    src={currentSong.audio}
                />
            )}
        </div>
    );
}

export default Musicplayer;
