import React, { useState, useEffect } from 'react';
import './App.css';
import Musicplayer from './Musicplayer.js';
import Loader from './Loader.js'; // Import your Loader component
import audioClip from './loader.mp3'; // Import your audio clip

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [audioPlayed, setAudioPlayed] = useState(false); // Track if audio has been played

  useEffect(() => {
    const audio = new Audio(audioClip);

    // Function to handle audio play
    const playAudio = () => {
      audio.play().catch(error => {
        console.error('Failed to play audio:', error);
      });
      setAudioPlayed(true);
    };

    // Play audio if it hasn't been played before
    if (!audioPlayed) {
      playAudio();
    } else {
      setIsLoading(false); // If audio has been played before, stop loader
    }

    // Set timeout to stop loader after 5 seconds
    const timer = setTimeout(() => {
      setIsLoading(false);
      audio.pause(); // Pause the audio when loading is complete
    }, 5000);

    // Clean up timeout and pause audio on component unmount
    return () => {
      clearTimeout(timer);
      audio.pause();
    };
  }, [audioPlayed]);

  return (
    <>
      {isLoading ? (
        // Show the loader component while isLoading is true
        <Loader />
      ) : (
        // Show the music player app when isLoading becomes false
        <Musicplayer />
      )}
    </>
  );
}

export default App;
