import React, { useState, useRef, useEffect } from 'react';
import "./Musicplayer.css";

const MusicPlayer = () => {
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentTime, setCurrentTime] = useState(0);
    const [duration, setDuration] = useState(0);
    const [songs, setSongs] = useState([]);
    const [currentSongIndex, setCurrentSongIndex] = useState(0); // Add state for current song index
    const audioRef = useRef(null);
    const [audioLoaded, setAudioLoaded] = useState(false);

    useEffect(() => {
        fetch('http://localhost:5000/api/songs/')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch songs');
                }
                return response.json();
            })
            .then(data => {
                setSongs(data);
                if (data.length > 0) {
                    audioRef.current.src = `http://localhost:5000${data[currentSongIndex].audio}`;
                    // No need to auto-play here
                    // audioRef.current.play();
                }
            })
            .catch(error => console.error('Error fetching songs:', error));
    }, [currentSongIndex]); // Fetch songs whenever currentSongIndex changes

    // Function to handle playing the current song
    const handlePlay = () => {
        const playPromise = audioRef.current.play();
        if (playPromise !== undefined) {
            playPromise
                .then(() => {
                    setIsPlaying(true);
                })
                .catch(error => {
                    console.error('Playback failed:', error);
                });
        }
    };

    // UseEffect to set up audio event listeners
    useEffect(() => {
        if (audioRef.current) {
            audioRef.current.onloadeddata = () => {
                setAudioLoaded(true);
            };
            audioRef.current.onplay = () => {
                setIsPlaying(true);
            };
            audioRef.current.onpause = () => {
                setIsPlaying(false);
            };
            audioRef.current.ontimeupdate = () => {
                setCurrentTime(audioRef.current.currentTime);
            };
            audioRef.current.onended = () => {
                // Play next song when current song ends
                handleNext();
            };
        }
    }, [audioRef]);

    // Function to play the next song
    const handleNext = () => {
        const newIndex = (currentSongIndex + 1) % songs.length;
        setCurrentSongIndex(newIndex);
    };

    // Function to play the previous song
    const handlePrevious = () => {
        const newIndex = (currentSongIndex - 1 + songs.length) % songs.length;
        setCurrentSongIndex(newIndex);
    };

    // Function to handle play/pause toggle
    const playPauseToggle = () => {
        if (isPlaying) {
            audioRef.current.pause();
        } else {
            handlePlay();
        }
    };

    // Function to handle volume change
    const handleVolumeChange = (e) => {
        const newVolume = parseFloat(e.target.value);
        if (!isNaN(newVolume) && isFinite(newVolume)) {
            audioRef.current.volume = newVolume;
        }
    };

    // Function to handle progress change
    const handleProgressChange = (e) => {
        audioRef.current.currentTime = e.target.value * duration / 100;
    };

    return (
        <div className="music-container">
            <div className="music-body">
                <div className="layer">
                    <div className="volume-slider">
                        <span className="bar"></span>
                        <input type='range' min='0.01' max='1' value='1' step='0.01' className="volumerange" onChange={handleVolumeChange} />
                    </div>
                </div>
                <div className="top-icons">
                    <button className="volume btn" onClick={handleVolumeChange}>
                        <i id="volumeicon" className="fas fa-volume-up"></i>
                    </button>
                    <button className="list btn">
                        <i className="fas fa-list"></i>
                    </button>
                </div>
                <div className="music-img">
                    <img src="https://images.genius.com/ee202c6f724ffd4cf61bd01a205eeb47.1000x1000x1.jpg" alt="song-images" id="img" />
                </div>
                <div className="music-info">
                    <h2 id="title">Yıldız Tozu</h2>
                </div>
                <audio
                id="audio"
                ref={audioRef}
                onLoadedData={() => setAudioLoaded(true)}
                onTimeUpdate={() => setCurrentTime(audioRef.current.currentTime)}
                onDurationChange={() => setDuration(audioRef.current.duration)}
            ></audio>
                <div className="music-progress">
                    <div className="progress-bar">
                        <span className="progress-line" style={{ width: `${(currentTime / duration) * 100}%` }}></span>
                        <input type='range' min='0' max='100' value={(currentTime / duration) * 100} className="progress" onChange={handleProgressChange} />
                    </div>
                    <div className="duration">
                        <span className="current-time">{formatTime(currentTime)}</span>
                        <span className="duration-time">{formatTime(duration)}</span>
                    </div>
                </div>
                <div className="music-controls">
                    <button className="repeat btn">
                        <i className="fas fa-redo-alt"></i>
                    </button>
                    <div className="main-controls">
                        <button className="prevbtn">
                            <i className="fas fa-backward"></i>
                        </button>
                        <button className="playpause" onClick={playPauseToggle}>
    <i id="playpause-btn" className={`fas ${isPlaying ? 'fa-pause' : 'fa-play'}`}></i>
</button>
                        <button className="nextbtn">
                            <i className="fas fa-forward"></i>
                        </button>
                    </div>
                    <button className="like btn">
                        <i id="likeicon" className="far fa-heart"></i>
                    </button>
                </div>
                <div className="songs-list">
                    <button className="list-close btn">
                        <i className="fas fa-times"></i>
                    </button>
                </div>
            </div>
        </div>
    );
};

const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
};

export default MusicPlayer;
