import React, { useState, useEffect } from "react";
import "./Musicplayer.css";
import { FaPlay, FaPause, FaStepForward, FaStepBackward } from "react-icons/fa";

function Musicplayer() {
  const [songs, setSongs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [currentSong, setCurrentSong] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [audioRef, setAudioRef] = useState(null);

  const fetchSong = (songId) => {
    fetch(`http://localhost:5000/api/songs/${songId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch song");
        }
        return response.json();
      })
      .then((data) => {
        setCurrentSong(data);
        setIsPlaying(true);
      })
      .catch((error) => console.error("Error fetching song:", error));
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleSongClick = (song) => {
    fetchSong(song.id);
  };

  const handlePlayPauseClick = () => {
    if (currentSong) {
      if (!audioRef.src) {
        setAudioRef(document.createElement("audio"));
        audioRef.src = `http://localhost:5000${currentSong.audio}`;
      }
      setIsPlaying((prevState) => !prevState);
    }
  };

  // Placeholder for handlePreviousClick function
  const handlePreviousClick = () => {
    // Implement logic for handling previous song
  };

  // Placeholder for handleNextClick function
  const handleNextClick = () => {
    // Implement logic for handling next song
  };

  // Placeholder for handleTimeUpdate function
  const handleTimeUpdate = () => {
    // Implement logic for handling time update
  };

  return (
    <div className="music-player">
      <h1 className="title">GS Music Player</h1>
      <div className="controls">
        <input
          type="text"
          placeholder="Search for a song..."
          value={searchTerm}
          onChange={handleSearch}
        />
        <select value={selectedCategory} onChange={handleCategoryChange}>
          <option value="All">All Categories</option>
          <option value="English">English</option>
          <option value="Hindi">Hindi</option>
          <option value="Punjabi">Punjabi</option>
          <option value="Odia">Odia</option>
        </select>
      </div>
      <div className="cd-container">
        {currentSong && (
          <div className={`cd ${isPlaying ? "playing" : ""}`}>
            <img
              className="cd-image"
              src={currentSong.image}
              alt="Album Cover"
            />
            <div className="song-info">
              <p className="song-name">{currentSong.title}</p>
              <p className="artist-name">{currentSong.artist}</p>
            </div>
            <div className="controls">
              <FaStepBackward
                className="control-button"
                onClick={handlePreviousClick}
              />
              {isPlaying ? (
                <FaPause
                  className="control-button"
                  onClick={handlePlayPauseClick}
                />
              ) : (
                <FaPlay
                  className="control-button"
                  onClick={handlePlayPauseClick}
                />
              )}
              <FaStepForward
                className="control-button"
                onClick={handleNextClick}
              />
            </div>
          </div>
        )}
      </div>
      <ul className="song-list">
        {songs.map((song) => (
          <li key={song.id} onClick={() => handleSongClick(song)}>
            {song.title} - {song.artist}
          </li>
        ))}
      </ul>
      {currentSong && (
        <audio
          className="audio-player"
          ref={(audio) => setAudioRef(audio)}
          controls
          autoPlay={isPlaying}
          src={`http://localhost:5000${currentSong.audio}`}
          // src="http://localhost:5000/uploads_Songs/1.mp3"
          onTimeUpdate={handleTimeUpdate}
        />
      )}
    </div>
  );
}

export default Musicplayer;
