import React, { useState, useRef, useEffect } from "react";
import "./Musicplayer.css";

const MusicPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [currentSongImage, setCurrentSongImage] = useState("");
  const [songs, setSongs] = useState([]);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const audioRef = useRef(null);
  const [audioLoaded, setAudioLoaded] = useState(false);

  
 
  useEffect(() => {
    fetch('http://localhost:5000/api/songs/')
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to fetch songs');
            }
            return response.json();
        })
        .then(data => {
            console.log("Fetched songs:", data); // Log fetched songs to console
            setSongs(data);
        })
        .catch(error => console.error('Error fetching songs:', error));
}, []);



useEffect(() => {
    const lastSongId = localStorage.getItem('lastSongId');
    if (lastSongId) {
        const lastSong = songs.find(song => song.id === parseInt(lastSongId));
        if (lastSong) {
            setCurrentSong(lastSong);
        }
    }
}, [songs]);

const handleSearch = (event) => {
    setSearchTerm(event.target.value);
};

const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
};

const handleSongClick = (song) => {
    setCurrentSong(song);
    setIsPlaying(true);
    localStorage.setItem('lastSongId', song.id);
};

const handlePlayPauseClick = () => {
    setIsPlaying(prevState => !prevState);
};

const handleNextClick = () => {
    const currentIndex = songs.findIndex(song => song.id === currentSong.id);
    const nextIndex = (currentIndex + 1) % songs.length;
    setCurrentSong(songs[nextIndex]);
    setIsPlaying(true);
    localStorage.setItem('lastSongId', songs[nextIndex].id);
};

const handlePreviousClick = () => {
    const currentIndex = songs.findIndex(song => song.id === currentSong.id);
    const previousIndex = (currentIndex - 1 + songs.length) % songs.length;
    setCurrentSong(songs[previousIndex]);
    setIsPlaying(true);
    localStorage.setItem('lastSongId', songs[previousIndex].id);
};

const handleTimeUpdate = () => {
    // Update your progress bar logic here based on audioRef.currentTime and audioRef.duration
};
  // Function to handle progress change
  const handleProgressChange = (e) => {
    audioRef.current.currentTime = (e.target.value * duration) / 100;
  };

  return (
    <div className="music-container">
      <div className="music-body">
        <div className="layer">
          <div className="volume-slider">
            <span className="bar"></span>
            <input
              type="range"
              min="0.01"
              max="1"
              value="1"
              step="0.01"
              className="volumerange"
              onChange={handleVolumeChange}
            />
          </div>
        </div>
        <div className="top-icons">
          <button className="volume btn" onClick={handleVolumeChange}>
            <i id="volumeicon" className="fas fa-volume-up"></i>
          </button>
          <button className="list btn">
            <i className="fas fa-list"></i>
          </button>
        </div>
        <div className="music-img">
          <img src={currentSongImage} alt="song-images" id="img" />
        </div>
        <div className="music-info">
          <h2 id="title">Yıldız Tozu</h2>
        </div>
        <audio
          id="audio"
          ref={audioRef}
          onLoadedData={() => setAudioLoaded(true)}
          onTimeUpdate={() => setCurrentTime(audioRef.current.currentTime)}
          onDurationChange={() => setDuration(audioRef.current.duration)}
        ></audio>
        <div className="music-progress">
          <div className="progress-bar">
            <span
              className="progress-line"
              style={{ width: `${(currentTime / duration) * 100}%` }}
            ></span>
            <input
              type="range"
              min="0"
              max="100"
              value={(currentTime / duration) * 100}
              className="progress"
              onChange={handleProgressChange}
            />
          </div>
          <div className="duration">
            <span className="current-time">{formatTime(currentTime)}</span>
            <span className="duration-time">{formatTime(duration)}</span>
          </div>
        </div>
        <div className="music-controls">
          <button className="repeat btn">
            <i className="fas fa-redo-alt"></i>
          </button>
          <div className="main-controls">
            <button className="prevbtn" onClick={handlePrevious}>
              <i className="fas fa-backward"></i>
            </button>
            <button className="playpause" onClick={playPauseToggle}>
              <i
                id="playpause-btn"
                className={`fas ${isPlaying ? "fa-pause" : "fa-play"}`}
              ></i>
            </button>
            <button className="nextbtn" onClick={handleNext}>
              <i className="fas fa-forward"></i>
            </button>
          </div>
          <button className="like btn">
            <i id="likeicon" className="far fa-heart"></i>
          </button>
        </div>
        <div className="songs-list">
          <button className="list-close btn">
            <i className="fas fa-times"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

const formatTime = (time) => {
  const minutes = Math.floor(time / 60);
  const seconds = Math.floor(time % 60);
  return `${String(minutes).padStart(2, "0")}:${String(seconds).padStart(
    2,
    "0"
  )}`;
};

export default MusicPlayer;
