import React, { useState, useRef } from 'react';
import "./MusicPlayer"

const MusicPlayer = () => {
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentTime, setCurrentTime] = useState(0);
    const [duration, setDuration] = useState(0);
    const audioRef = useRef(null);

    const playPauseToggle = () => {
        if (isPlaying) {
            audioRef.current.pause();
        } else {
            audioRef.current.play();
        }
        setIsPlaying(!isPlaying);
    };

    const handleTimeUpdate = () => {
        setCurrentTime(audioRef.current.currentTime);
    };

    const handleDurationChange = () => {
        setDuration(audioRef.current.duration);
    };

    const handleVolumeChange = (e) => {
        audioRef.current.volume = e.target.value;
    };

    const handleProgressChange = (e) => {
        audioRef.current.currentTime = e.target.value;
    };

    return (
        <div className="music-container">
            <div className="music-body">
                <div className="layer">
                    <div className="volume-slider">
                        <span className="bar"></span>
                        <input type='range' min='0.01' max='1' value='1' step='0.01' className="volumerange" onChange={handleVolumeChange} />
                    </div>
                </div>
                <div className="top-icons">
                    <button className="volume btn" onClick={handleVolumeChange}>
                        <i id="volumeicon" className="fas fa-volume-up"></i>
                    </button>
                    <button className="list btn">
                        <i className="fas fa-list"></i>
                    </button>
                </div>
                <div className="music-img">
                    <img src="https://images.genius.com/ee202c6f724ffd4cf61bd01a205eeb47.1000x1000x1.jpg" alt="song-images" id="img" />
                </div>
                <div className="music-info">
                    <h2 id="title">Yıldız Tozu</h2>
                </div>
                <audio
                    src="https://raw.githubusercontent.com/ustabasiibrahim/music-player/master/assets/music/1.mp3"
                    id="audio"
                    ref={audioRef}
                    onTimeUpdate={handleTimeUpdate}
                    onDurationChange={handleDurationChange}
                ></audio>
                <div className="music-progress">
                    <div className="progress-bar">
                        <span className="progress-line" style={{ width: `${(currentTime / duration) * 100}%` }}></span>
                        <input type='range' min='0' max='100' value={(currentTime / duration) * 100} className="progress" onChange={handleProgressChange} />
                    </div>
                    <div className="duration">
                        <span className="current-time">{formatTime(currentTime)}</span>
                        <span className="duration-time">{formatTime(duration)}</span>
                    </div>
                </div>
                <div className="music-controls">
                    <button className="repeat btn">
                        <i className="fas fa-redo-alt"></i>
                    </button>
                    <div className="main-controls">
                        <button className="prevbtn">
                            <i className="fas fa-backward"></i>
                        </button>
                        <button className="playpause" onClick={playPauseToggle}>
                            <i id="playpause-btn" className={`fas ${isPlaying ? 'fa-pause' : 'fa-play'}`}></i>
                        </button>
                        <button className="nextbtn">
                            <i className="fas fa-forward"></i>
                        </button>
                    </div>
                    <button className="like btn">
                        <i id="likeicon" className="far fa-heart"></i>
                    </button>
                </div>
                <div className="songs-list">
                    <button className="list-close btn">
                        <i className="fas fa-times"></i>
                    </button>
                </div>
            </div>
        </div>
    );
};

const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
};

export default MusicPlayer;
